import React, { useState, useEffect, useRef, useCallback } from 'react';
import { CircularProgress } from '@mui/material';
import EnhancedTable from './EnhancedTable';
import './App.css';

function ChatComponent({ connectionStatus, activeSessionId }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const chatWindowRef = useRef(null);

  useEffect(() => {
    const initializeChat = async () => {
      if (activeSessionId) {
        try {
          const response = await fetch('/api/query/history', {
            headers: {
              'X-Session-ID': activeSessionId,
            },
          });

          if (response.ok) {
            const history = await response.json();
            const formattedHistory = history.map((msg) => ({
              text: msg.message,
              isUser: msg.isUser,
              type: msg.type,
              sql: msg.generatedSql,
              tableData: msg.tableData,
            }));

            setMessages(formattedHistory);
          } else {
            setMessages([]);
          }
        } catch (error) {
          console.error('Error loading chat history:', error);
          setMessages([]);
        }
      } else {
        setMessages([]);
      }
    };

    initializeChat();
  }, [activeSessionId]);

  const scrollToBottom = useCallback(() => {
    if (messagesEndRef.current) {
      const chatWindow = chatWindowRef.current;
      const isScrolledToBottom =
          chatWindow.scrollHeight - chatWindow.scrollTop <=
          chatWindow.clientHeight + 100;

      if (isScrolledToBottom) {
        messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    const userMessage = { text: input, isUser: true, type: 'QUERY' };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const sessionId = activeSessionId;
      const response = await fetch('/api/query/nl-to-sql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Session-ID': sessionId,
        },
        body: JSON.stringify({ query: input }),
      });

      const data = await response.json();

      if (response.ok) {
        const aiMessage = {
          text: data.message,
          isUser: false,
          type: data.type || 'RESPONSE',
          sql: data.generatedSql,
          tableData: data.tableData,
        };
        setMessages((prev) => [...prev, aiMessage]);
      } else {
        throw new Error(data.message || 'Failed to process query');
      }
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        text: error.message || 'Sorry, there was an error processing your request.',
        isUser: false,
        type: 'ERROR',
      };
      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const getMessageClassName = useCallback((message) => {
    let baseClass = 'message';
    if (message.isUser) {
      baseClass += ' user-message';
    } else {
      baseClass += ' ai-message';
      if (message.type === 'ERROR') baseClass += ' error-message';
      if (message.type === 'SYSTEM') baseClass += ' system-message';
    }
    return baseClass;
  }, []);

  // Function to prepare data for EnhancedTable
  const prepareTableData = (tableData) => {
    if (!tableData || !tableData.headers || !tableData.rows) return null;

    const columns = tableData.headers.map((header) => ({
      field: header,
      headerName: header,
      width: 150,
    }));

    const rows = tableData.rows.map((row, index) => {
      const rowData = { id: index };
      tableData.headers.forEach((header, idx) => {
        rowData[header] = row[idx];
      });
      return rowData;
    });

    return { columns, rows };
  };

  return (
      <div className="chat-container">
        <div className="chat-window" ref={chatWindowRef}>
          {messages.map((message, index) => (
              <div key={index} className={getMessageClassName(message)}>
            <span className="message-icon">
              {message.isUser ? 'üë§' : message.type === 'ERROR' ? '‚ö†Ô∏è' : 'ü§ñ'}
            </span>
                <div className="message-content">
                  <div className="message-text">{message.text}</div>
                  {message.sql && (
                      <div className="sql-query">
                        <span className="sql-label">Generated SQL:</span>
                        <code>{message.sql}</code>
                      </div>
                  )}
                  {message.tableData && (
                      <div className="table-container">
                        {(() => {
                          const tableProps = prepareTableData(message.tableData);
                          return tableProps ? (
                              <EnhancedTable
                                  rows={tableProps.rows}
                                  columns={tableProps.columns}
                              />
                          ) : null;
                        })()}
                      </div>
                  )}
                </div>
              </div>
          ))}
          {isLoading && (
              <div className="loading-indicator">
                <CircularProgress size={24} color="inherit" />
              </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        <form onSubmit={handleSubmit} className="input-area">
          <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder={
                connectionStatus === 'success'
                    ? 'Type your query...'
                    : 'Connect to database first...'
              }
              disabled={connectionStatus !== 'success' || isLoading}
              className="w-full px-4 py-2.5 bg-gray-800 text-white rounded-lg
            border border-gray-700 focus:border-blue-500 focus:ring-1
            focus:ring-blue-500 hover:border-gray-600 transition-colors
            duration-200 placeholder-gray-400"
          />
          <button
              type="submit"
              disabled={connectionStatus !== 'success' || isLoading || !input.trim()}
              className="ml-2 px-6 py-2.5 bg-blue-600 text-white rounded-lg
            hover:bg-blue-700 focus:outline-none focus:ring-2
            focus:ring-blue-500 focus:ring-opacity-50 transition-colors
            duration-200 disabled:bg-gray-600 disabled:cursor-not-allowed"
          >
            {isLoading ? 'Processing...' : 'Send'}
          </button>
        </form>
      </div>
  );
}

export default ChatComponent;
