import React, { useState, useEffect } from 'react';
import './App.css';
import ChatComponent from './ChatComponent';

function App() {
  const [connectionStatus, setConnectionStatus] = useState('');
  const [dbConfig, setDbConfig] = useState({
    host: 'localhost',
    port: '3306',
    username: 'root',
    password: '',
    database: 'testdb',
  });
  const [sessions, setSessions] = useState([]);
  const [activeSessionId, setActiveSessionId] = useState(localStorage.getItem('sessionId') || null);

  useEffect(() => {
    testBackendConnection();
    fetchSessions();
  }, []);

  useEffect(() => {
    if (activeSessionId) {
      localStorage.setItem('sessionId', activeSessionId);
    }
  }, [activeSessionId]);

  const testBackendConnection = async () => {
    try {
      const response = await fetch('/api/database/test');
      if (response.ok) {
        console.log('Backend is reachable');
      } else {
        console.error('Failed to reach backend');
      }
    } catch (error) {
      console.error('Error testing backend connection:', error);
    }
  };

  const fetchSessions = async () => {
    try {
      const response = await fetch('/api/sessions');
      if (response.ok) {
        const data = await response.json();
        setSessions(data);
        if (!activeSessionId && data.length > 0) {
          setActiveSessionId(data[0].sessionId);
          localStorage.setItem('sessionId', data[0].sessionId);
        }
      } else {
        console.error('Failed to fetch sessions');
      }
    } catch (error) {
      console.error('Error fetching sessions:', error);
    }
  };

  const handleInputChange = (e) => {
    setDbConfig({ ...dbConfig, [e.target.name]: e.target.value });
  };

  const handleConnect = async () => {
    try {
      const sessionId = activeSessionId || 'session-' + Date.now();
      setActiveSessionId(sessionId);
      localStorage.setItem('sessionId', sessionId);

      const response = await fetch('/api/database/connect', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Session-ID': sessionId,
        },
        body: JSON.stringify(dbConfig),
      });

      if (response.ok) {
        setConnectionStatus('success');
        fetchSessions();
      } else {
        const errorText = await response.text();
        console.error('Connection failed:', errorText);
        setConnectionStatus('error');
      }
    } catch (error) {
      console.error('Connection error:', error);
      setConnectionStatus('error');
    }
  };

  const handleNewSession = () => {
    const newSessionId = 'session-' + Date.now();
    setActiveSessionId(newSessionId);
    localStorage.setItem('sessionId', newSessionId);
    setConnectionStatus('');
    setDbConfig({
      host: 'localhost',
      port: '3306',
      username: 'root',
      password: '',
      database: 'testdb',
    });
  };

  const handleSessionChange = (e) => {
    const sessionId = e.target.value;
    setActiveSessionId(sessionId);
    localStorage.setItem('sessionId', sessionId);
    setConnectionStatus('');
  };

  return (
      <div className="app-container">
        <div className="sidebar">
          <div className="sidebar-content">
            <div className="space-y-4">
              <h2 className="text-xl font-semibold text-white mb-4">Sessions</h2>

              {/* Enhanced New Session Button */}
              <button
                  onClick={handleNewSession}
                  className="w-full px-4 py-2.5 bg-blue-600 hover:bg-blue-700
                text-white rounded-lg transition-colors duration-200
                border border-blue-500 hover:border-blue-600
                focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50
                font-medium text-sm shadow-sm"
              >
                New Session
              </button>

              {/* Enhanced Session Selector */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-300">
                  Select Session
                </label>
                <select
                    value={activeSessionId || ''}
                    onChange={handleSessionChange}
                    className="w-full px-3 py-2.5 bg-gray-800 text-white
                  rounded-lg border border-gray-700
                  focus:border-blue-500 focus:ring-1 focus:ring-blue-500
                  hover:border-gray-600 transition-colors duration-200
                  appearance-none cursor-pointer
                  text-sm font-medium"
                >
                  <option value="" disabled className="bg-gray-800">
                    Select a session
                  </option>
                  {sessions.map((session) => (
                      <option
                          key={session.sessionId}
                          value={session.sessionId}
                          className="bg-gray-800 py-1"
                      >
                        {session.sessionId}
                      </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="mt-8">
              <h2 className="text-xl font-semibold text-white mb-4">Database Connection</h2>
              <p className="text-gray-400 text-sm mb-4">Connect to your MySQL database</p>

              {/* Enhanced form inputs */}
              {Object.entries(dbConfig).map(([key, value]) => (
                  <div key={key} className="form-group mb-4">
                    <label className="block text-sm font-medium text-gray-300 mb-1 capitalize">
                      {key}
                    </label>
                    <input
                        type={key === 'password' ? 'password' : 'text'}
                        name={key}
                        value={value}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 bg-gray-800 text-white
                    rounded-lg border border-gray-700
                    focus:border-blue-500 focus:ring-1 focus:ring-blue-500
                    hover:border-gray-600 transition-colors duration-200"
                    />
                  </div>
              ))}

              {/* Enhanced connect button */}
              <button
                  className={`w-full px-4 py-2.5 rounded-lg font-medium text-sm shadow-sm
                transition-colors duration-200 
                ${
                      connectionStatus === 'success'
                          ? 'bg-green-600 hover:bg-green-700 text-white'
                          : connectionStatus === 'error'
                              ? 'bg-red-600 hover:bg-red-700 text-white'
                              : 'bg-blue-600 hover:bg-blue-700 text-white'
                  }`}
                  onClick={handleConnect}
                  disabled={!activeSessionId}
              >
                {connectionStatus === 'success' ? 'Connected' : 'Connect'}
              </button>
            </div>
          </div>
        </div>

        <main className="main-content">
          <h1 className="text-2xl font-bold text-white mb-6">Chat with MySQL</h1>
          <ChatComponent
              connectionStatus={connectionStatus}
              activeSessionId={activeSessionId}
          />
        </main>
      </div>
  );
}

export default App;
